import{_ as o,o as e,c,V as l}from"./chunks/framework.364d6ed5.js";const a="/jdk.png",d="/jdk2.png",i="/jdk3.png",k=JSON.parse('{"title":"win10配置双JDK","description":"","frontmatter":{},"headers":[],"relativePath":"java/java/win10配置双jdk.md","filePath":"java/java/win10配置双jdk.md","lastUpdated":1704716480000}'),t={name:"java/java/win10配置双jdk.md"},s=l('<h1 id="win10配置双jdk" tabindex="-1">win10配置双JDK <a class="header-anchor" href="#win10配置双jdk" aria-label="Permalink to &quot;win10配置双JDK&quot;">​</a></h1><h2 id="_1-下载并安装jdk" tabindex="-1">1.下载并安装JDK <a class="header-anchor" href="#_1-下载并安装jdk" aria-label="Permalink to &quot;1.下载并安装JDK&quot;">​</a></h2><p>在<a href="https://www.oracle.com/java/technologies/downloads/" target="_blank" rel="noreferrer">官网</a>下载<code>JDK8</code>和<code>JDK17</code>，分别安装到指定目录。</p><img src="'+a+'"><div class="tip custom-block"><p class="custom-block-title">TIP</p><ul><li><ol><li>下载<code>exe</code>格式安装包</li></ol></li><li><ol start="2"><li><code>JDK8</code>会创建<code>JRE</code>文件夹，<code>JDK17</code>不会</li></ol></li></ul></div><h2 id="_2-配置java-home和class-path" tabindex="-1">2. 配置JAVA_HOME和CLASS_PATH <a class="header-anchor" href="#_2-配置java-home和class-path" aria-label="Permalink to &quot;2. 配置JAVA_HOME和CLASS_PATH&quot;">​</a></h2><p>在环境变量的系统变量中配置<code>JAVA_HOME</code>,<code>CLASS_PATH</code></p><ul><li><ol><li><code>JAVA_HOME</code>：<code>%JAVA17_HOME%</code></li></ol></li><li><ol start="2"><li><code>JAVA8_HOME</code>：<code>F:\\soft\\jdk</code></li></ol></li><li><ol start="3"><li><code>JAVA17_HOME</code>：<code>F:\\soft\\jdk-17.0.9</code></li></ol></li><li><ol start="4"><li><code>CLASS_PATH</code>：<code>.;%JAVA_HOME%\\lib\\dt.jar;%JAVA_HOME%\\lib\\tools.jar</code></li></ol></li></ul><img src="'+d+'"><div class="tip custom-block"><p class="custom-block-title">TIP</p><ul><li><ol><li><code>JAVA8_HOME</code>即<code>JDK8</code>的安装目录</li></ol></li><li><ol start="2"><li><code>JAVA17_HOME</code>即<code>JDK17</code>的安装目录</li></ol></li></ul></div><h2 id="_3-配置path" tabindex="-1">3. 配置path <a class="header-anchor" href="#_3-配置path" aria-label="Permalink to &quot;3. 配置path&quot;">​</a></h2><p>在环境变量的系统变量中配置<code>PATH</code>。</p><p>在<code>path</code>中添加两天记录<code>%JAVA_HOME%\\bin</code>，<code>%JAVA_HOME%\\jre\\bin</code></p><img src="'+i+'"><div class="tip custom-block"><p class="custom-block-title">TIP</p><ul><li><ol><li><code>%JAVA_HOME%\\bin</code>一定要放在第一行</li></ol></li></ul></div><h2 id="_4-验证是否配置成功" tabindex="-1">4. 验证是否配置成功 <a class="header-anchor" href="#_4-验证是否配置成功" aria-label="Permalink to &quot;4. 验证是否配置成功&quot;">​</a></h2><p>修改<code>JAVA_HOME</code>的值为<code>JAVA8_HOME</code>或<code>JAVA17_HOME</code>即可动态切换<code>JD</code>K版本。在<code>cmd</code>窗口中输入<code>java -version</code>查看当前JDK版本。</p><div class="tip custom-block"><p class="custom-block-title">TIP</p><ul><li><ol><li>修改<code>JAVA_HOME</code>后，需要重新重新打开<code>cmd</code>窗口</li></ol></li></ul></div>',18),r=[s];function _(n,p,A,h,m,u){return e(),c("div",null,r)}const j=o(t,[["render",_]]);export{k as __pageData,j as default};
