import{_ as t,o as e,c as l,V as s}from"./chunks/framework.364d6ed5.js";const A=JSON.parse('{"title":"权限管理","description":"","frontmatter":{"title":"权限管理","date":"2022-09-28T18:44:55.000Z","permalink":"/pages/00c5f2/","categories":["java","mysql"],"tags":[null]},"headers":[],"relativePath":"java/mysql/权限管理.md","filePath":"java/mysql/权限管理.md","lastUpdated":1685806707000}'),a={name:"java/mysql/权限管理.md"},n=s(`<h1 id="权限管理" tabindex="-1">权限管理 <a class="header-anchor" href="#权限管理" aria-label="Permalink to &quot;权限管理&quot;">​</a></h1><h2 id="_1-mysql中的权限" tabindex="-1">1. MySql中的权限 <a class="header-anchor" href="#_1-mysql中的权限" aria-label="Permalink to &quot;1. MySql中的权限&quot;">​</a></h2><table><thead><tr><th style="text-align:left;">权限</th><th style="text-align:center;">意义</th></tr></thead><tbody><tr><td style="text-align:left;">ALL [PRIVILEGES]</td><td style="text-align:center;">设置除 GRANT OPTION 之外的所有简单权限</td></tr><tr><td style="text-align:left;">ALTER</td><td style="text-align:center;">允许使用 ALTER TABLE</td></tr><tr><td style="text-align:left;">ALTER ROUTINE</td><td style="text-align:center;">更改或取消已存储的子程序</td></tr><tr><td style="text-align:left;">CREATE</td><td style="text-align:center;">允许使用 CREATE TABLE</td></tr><tr><td style="text-align:left;">CREATE ROUTINE</td><td style="text-align:center;">创建已存储的子程序</td></tr><tr><td style="text-align:left;">CREATE TEMPORARY TABLES</td><td style="text-align:center;">允许使用 CREATE TEMPORARY TABLE</td></tr><tr><td style="text-align:left;">CREATE USER</td><td style="text-align:center;">允许使用 CREATE USER,DROP USER,RENAME USER和REVOKE ALL PRIVILEGES</td></tr><tr><td style="text-align:left;">CREATE VIEW</td><td style="text-align:center;">允许使用 CREATE VIEW</td></tr><tr><td style="text-align:left;">DELETE</td><td style="text-align:center;">允许使用 DELETE</td></tr><tr><td style="text-align:left;">DROP</td><td style="text-align:center;">允许使用 DROP TABLE</td></tr><tr><td style="text-align:left;">EXECUTE</td><td style="text-align:center;">允许用户运行已存储的子程序</td></tr><tr><td style="text-align:left;">FILE</td><td style="text-align:center;">允许使用SELECT .. INTO OUTFILE 和 LOAD DATA INFILE</td></tr><tr><td style="text-align:left;">INDEX</td><td style="text-align:center;">允许使用 CREATE INDEX和 DROP INDEX</td></tr><tr><td style="text-align:left;">INSERT</td><td style="text-align:center;">允许使用 INSERT</td></tr><tr><td style="text-align:left;">LOCK TABLES</td><td style="text-align:center;">允许对您拥有 SELECT 权限的表使用 LOCK TABLES</td></tr><tr><td style="text-align:left;">PROCESS</td><td style="text-align:center;">允许使用 SHOW FULL PROCESSLIST</td></tr><tr><td style="text-align:left;">REFERENCES</td><td style="text-align:center;">未被实施</td></tr><tr><td style="text-align:left;">RELOAD</td><td style="text-align:center;">允许使用FLUSH</td></tr><tr><td style="text-align:left;">REPLICATION CLIENT</td><td style="text-align:center;">允许用户询问从属服务器或主服务器的地址</td></tr><tr><td style="text-align:left;">REPLICATION SLAVE</td><td style="text-align:center;">用于复制型从属服务器（从主服务器中读取二进制日志事件）</td></tr><tr><td style="text-align:left;">SELECT</td><td style="text-align:center;">允许使用 SELECT</td></tr><tr><td style="text-align:left;">SHOW DATABASES</td><td style="text-align:center;">SHOW DATABASES显示所有数据库</td></tr><tr><td style="text-align:left;">SHOW VIEW</td><td style="text-align:center;">允许使用 SHOW CREATE VIEW</td></tr><tr><td style="text-align:left;">SHUTDOWN</td><td style="text-align:center;">允许使用 mysqladmin shutdown</td></tr><tr><td style="text-align:left;">SUPER</td><td style="text-align:center;">允许使用 CHANGE MASTER ,KILL,PURGE MASTER LOGS 和 SET GLOBAL语句，mysqladmin debug命令；允许您连接（一次），即使达到 max_connections</td></tr><tr><td style="text-align:left;">UPDATE</td><td style="text-align:center;">允许使用 UPDATE</td></tr><tr><td style="text-align:left;">USAGE</td><td style="text-align:center;">&quot;无权限&quot;的同义词</td></tr><tr><td style="text-align:left;">GRANT OPTION</td><td style="text-align:center;">允许授予权限</td></tr></tbody></table><h2 id="_2-给用户授权" tabindex="-1">2. 给用户授权 <a class="header-anchor" href="#_2-给用户授权" aria-label="Permalink to &quot;2. 给用户授权&quot;">​</a></h2><h3 id="_2-1-语法" tabindex="-1">2.1 语法 <a class="header-anchor" href="#_2-1-语法" aria-label="Permalink to &quot;2.1 语法&quot;">​</a></h3><div class="language-sql line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">sql</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#F78C6C;">grant</span><span style="color:#A6ACCD;"> 权限列表 </span><span style="color:#F78C6C;">on</span><span style="color:#A6ACCD;"> 库.对象名 </span><span style="color:#F78C6C;">to</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">用户名</span><span style="color:#89DDFF;">&#39;</span><span style="color:#A6ACCD;">@</span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">登录位置</span><span style="color:#89DDFF;">&#39;</span><span style="color:#A6ACCD;"> [identified by &#39;密码&#39;]</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><ul><li><ol><li>库.对象名</li></ol></li></ul><p>这个对象名可以是<strong>表</strong>，<strong>视图</strong>，<strong>触发器</strong>，也可以是&quot;函数&quot;</p><ul><li><ol start="2"><li>权限列表，多个权限用逗号分开</li></ol></li></ul><div class="language-sql line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">sql</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#F78C6C;">grant</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">select</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">on</span><span style="color:#A6ACCD;"> ....</span></span>
<span class="line"></span>
<span class="line"><span style="color:#F78C6C;">grant</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">select</span><span style="color:#A6ACCD;">,</span><span style="color:#F78C6C;">delete</span><span style="color:#A6ACCD;">,</span><span style="color:#F78C6C;">create</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">on</span><span style="color:#A6ACCD;"> ...</span></span>
<span class="line"></span>
<span class="line"><span style="color:#F78C6C;">grant</span><span style="color:#A6ACCD;"> all [privileges] </span><span style="color:#F78C6C;">on</span><span style="color:#A6ACCD;"> ...  </span><span style="color:#89DDFF;">//</span><span style="color:#A6ACCD;"> 表示赋予该用户在该对象上的所有权限</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br></div></div><ul><li><ol start="3"><li>特别说明</li></ol></li></ul><p><code>*.*</code> : 代表本系统中的所有数据库的所有对象（表，视图，存储过程） 库.* : 表示某个数据库中的所有数据对象（表，视图，存储过程等）</p><ul><li><ol start="4"><li>identified by 可以省略，也可以写出</li></ol><ul><li>如果用户存在，就是修改该用户的密码</li><li>如果该用户不存在，就是创建该用户</li></ul></li></ul><h2 id="_3-回收用户授权" tabindex="-1">3. 回收用户授权 <a class="header-anchor" href="#_3-回收用户授权" aria-label="Permalink to &quot;3. 回收用户授权&quot;">​</a></h2><h3 id="_3-1-基本语法" tabindex="-1">3.1 基本语法 <a class="header-anchor" href="#_3-1-基本语法" aria-label="Permalink to &quot;3.1 基本语法&quot;">​</a></h3><div class="language-sql line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">sql</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#F78C6C;">revoke</span><span style="color:#A6ACCD;"> 权限列表 </span><span style="color:#F78C6C;">on</span><span style="color:#A6ACCD;"> 库.对象名 </span><span style="color:#F78C6C;">from</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">用户名</span><span style="color:#89DDFF;">&#39;</span><span style="color:#A6ACCD;">@</span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">登录位置</span><span style="color:#89DDFF;">&#39;</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><h2 id="_4-权限生效指令" tabindex="-1">4. 权限生效指令 <a class="header-anchor" href="#_4-权限生效指令" aria-label="Permalink to &quot;4. 权限生效指令&quot;">​</a></h2><p>如果权限没有生效，可以执行下面的命令，基本语法：</p><p><code>FLUSH PRIVILEGES</code></p>`,19),r=[n];function o(d,i,p,c,y,C){return e(),l("div",null,r)}const g=t(a,[["render",o]]);export{A as __pageData,g as default};
