import{_ as a,o as s,c as l,V as e}from"./chunks/framework.364d6ed5.js";const C=JSON.parse('{"title":"用户管理","description":"","frontmatter":{"title":"用户管理","date":"2022-09-28T18:44:20.000Z","permalink":"/pages/9c8c35/","categories":["java","mysql"],"tags":[null]},"headers":[],"relativePath":"java/mysql/用户管理.md","filePath":"java/mysql/用户管理.md","lastUpdated":1685806707000}'),o={name:"java/mysql/用户管理.md"},n=e('<h1 id="用户管理" tabindex="-1">用户管理 <a class="header-anchor" href="#用户管理" aria-label="Permalink to &quot;用户管理&quot;">​</a></h1><h2 id="_1-mysql-用户" tabindex="-1">1. mysql 用户 <a class="header-anchor" href="#_1-mysql-用户" aria-label="Permalink to &quot;1. mysql 用户&quot;">​</a></h2><p><code>mysql</code>中的用户，都存在系统数据库<code>mysql</code>中的<code>user</code>表中。其中<code>user</code>表有三个比较重要的<strong>字段</strong>需要了解</p><p>当我们做项目开发时，可以根据不同的开发人员，赋给他相应的<code>Mysql</code>操作权限。所以，<code>Mysql</code>数据库管理人员（<code>root</code>），根据需要创建不同的用户，赋给相应的权限，供给人员使用。</p><p><strong>不同的数据库用户，登录到数据库后，根据相应的权限，可以操作的数据库和数据对象（表，视图，触发器）都不一样</strong></p><h3 id="_1-1-host" tabindex="-1">1.1 host <a class="header-anchor" href="#_1-1-host" aria-label="Permalink to &quot;1.1 host&quot;">​</a></h3><p>允许登录的<code>&quot;位置&quot;</code>，<code>localhost</code>表示该用户只允许本机登录，也可以指定<code>ip</code>地址，比如<code>192.168.1.100</code></p><h3 id="_1-2-user" tabindex="-1">1.2 user <a class="header-anchor" href="#_1-2-user" aria-label="Permalink to &quot;1.2 user&quot;">​</a></h3><p>用户名</p><h3 id="_1-3-authentication-string" tabindex="-1">1.3 authentication_string <a class="header-anchor" href="#_1-3-authentication-string" aria-label="Permalink to &quot;1.3 authentication_string&quot;">​</a></h3><p>登录密码，是通过<code>mysql</code>的<code>password()</code>函数加密之后的密码。</p><h2 id="_2-管理用户" tabindex="-1">2 管理用户 <a class="header-anchor" href="#_2-管理用户" aria-label="Permalink to &quot;2 管理用户&quot;">​</a></h2><h3 id="_2-1-创建用户" tabindex="-1">2.1 创建用户 <a class="header-anchor" href="#_2-1-创建用户" aria-label="Permalink to &quot;2.1 创建用户&quot;">​</a></h3><p>创建用户，同时指定密码</p><ul><li><ol><li>语法</li></ol></li></ul><div class="language-sql line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">sql</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#F78C6C;">create</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">user</span><span style="color:#A6ACCD;"> &#39;</span><span style="color:#82AAFF;">用户名</span><span style="color:#A6ACCD;">&#39;@</span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">允许登录位置</span><span style="color:#89DDFF;">&#39;</span><span style="color:#A6ACCD;"> identified </span><span style="color:#F78C6C;">by</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">密码</span><span style="color:#89DDFF;">&#39;</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><ul><li><ol start="2"><li>演示</li></ol></li></ul><p><code>CREATE USER &#39;zhug&#39;@&#39;localhost&#39; IDENTIFIED BY &#39;123456&#39;</code></p><h3 id="_2-2-删除用户" tabindex="-1">2.2 删除用户 <a class="header-anchor" href="#_2-2-删除用户" aria-label="Permalink to &quot;2.2 删除用户&quot;">​</a></h3><ul><li><ol><li>语法</li></ol></li></ul><div class="language-sql line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">sql</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#F78C6C;">drop</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">user</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">用户名</span><span style="color:#89DDFF;">&#39;</span><span style="color:#A6ACCD;">@</span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">允许登录位置</span><span style="color:#89DDFF;">&#39;</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><ul><li><ol start="2"><li>演示</li></ol></li></ul><p><code>DROP USER &#39;zhug&#39;@&#39;localhost&#39;</code></p><h3 id="_2-3-删除用户" tabindex="-1">2.3. 删除用户 <a class="header-anchor" href="#_2-3-删除用户" aria-label="Permalink to &quot;2.3. 删除用户&quot;">​</a></h3><h4 id="_2-3-1-修改自己的密码" tabindex="-1">2.3.1 修改自己的密码 <a class="header-anchor" href="#_2-3-1-修改自己的密码" aria-label="Permalink to &quot;2.3.1 修改自己的密码&quot;">​</a></h4><ul><li><ol><li>语法</li></ol></li></ul><div class="language-sql line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">sql</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#F78C6C;">SET</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">PASSWORD</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">password</span><span style="color:#A6ACCD;">(</span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">密码</span><span style="color:#89DDFF;">&#39;</span><span style="color:#A6ACCD;">)</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><h4 id="_2-3-2-修改他人的密码" tabindex="-1">2.3.2 修改他人的密码 <a class="header-anchor" href="#_2-3-2-修改他人的密码" aria-label="Permalink to &quot;2.3.2 修改他人的密码&quot;">​</a></h4><p>需要有修改用户密码权限</p><div class="language-sh line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">SET</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">PASSWORD</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">FOR</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">用户名</span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">@</span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">登录位置</span><span style="color:#89DDFF;">&#39;</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">=</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">password</span><span style="color:#89DDFF;">(</span><span style="color:#FFCB6B;">&#39;密码&#39;</span><span style="color:#89DDFF;">)</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><h3 id="_2-4-细节" tabindex="-1">2.4 细节 <a class="header-anchor" href="#_2-4-细节" aria-label="Permalink to &quot;2.4 细节&quot;">​</a></h3><ul><li><ol><li>在创建用户的时候，如果不指定<code>HOST</code>,则为<code>%</code>，<code>%</code>表示所有<code>IP</code>都有连接权限</li></ol></li></ul><p><code>CREATE USER xxx</code></p><ul><li><ol start="2"><li>也可以这样指定</li></ol></li></ul><p><code>create user &#39;xxx&#39;@&#39;192.168.1.%&#39;</code>，表示<code>xxx</code>用户在<code>192.168.1.*</code>的<code>ip</code>可以登录<code>mysql</code></p><ul><li><ol start="3"><li>在删除用户的时候，如果<code>HOST</code>不是<code>%</code>,需要明确指定 <code>&#39;用户&#39;@&#39;host&#39;</code>值</li></ol></li></ul>',36),t=[n];function p(r,c,i,d,h,u){return s(),l("div",null,t)}const D=a(o,[["render",p]]);export{C as __pageData,D as default};
